import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-notifications-manager',
  templateUrl: './notifications-manager.component.html',
  styleUrls: ['./notifications-manager.component.scss']
})
export class NotificationsManagerComponent implements OnInit {

  @Input()
  notificationsCount = 0

  @Output()
  notiChange = new EventEmitter<number>()

  constructor() { }

  ngOnInit(): void {
  }

  addNotification() {
    this.notificationsCount++;
    this.notiChange.emit(this.notificationsCount);
  }

  removeNotification() {
    if (this.notificationsCount == 0) {
      return;
    }
    this.notificationsCount--;
  }

  resetCount() {
    this.notificationsCount = 0;
  }

}
<div class="notif-manager">
  <div class="notif-manager__count">
    Notifications Count: {{notificationsCount}}
  </div>
  <div class="notif-manager__actions">
    <button (click)="addNotification()" class="notif-manager__actions__button">
      Add Notification
    </button>
    <button (click)="removeNotification()" class="notif-manager__actions__button">
      Remove Notification
    </button>
    <button (click)="resetCount()" class="notif-manager__actions__button">
      Reset Count
    </button>
  </div>
</div>
