import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-version-control',
  templateUrl: './version-control.component.html',
  styleUrls: ['./version-control.component.scss']
})
export class VersionControlComponent implements OnInit {
  versionForm = new FormGroup({
    version: new FormControl('', [Validators.pattern('([0-9]+)\.([0-9]+)\.([0-9]+)')])
  })
  versionName = '0.0.0';
  constructor() { }

  ngOnInit(): void {
    this.versionForm.get('version').setValue(this.versionName);
  }

  formSubmit() {
    if (!this.versionForm.valid) {
      alert('Invalid version value. Please use symantic versioning')
      return;
    }
    this.versionName = this.versionForm.get('version').value;
  }

}
<form [formGroup]="versionForm" (ngSubmit)="formSubmit()">
  <div class="form-group">
    <label for="versionNumber">Version Number</label>
    <input formControlName="version" type="text" class="form-control" id="versionNumber" aria-describedby="versionHelp" placeholder="Enter version number">
    <small id="versionHelp" class="form-text text-muted">Use semantic versioning (x.x.x)</small>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
<app-vc-logs [vName]="versionName"></app-vc-logs>
